import React, { useState } from 'react';
import supabase from '../../supabase'; // Assurez-vous que le chemin est correct

function Inscription() {
  const [email, setEmail] = useState('');
  const [motDePasse, setMotDePasse] = useState('');
  const [error, setError] = useState(null);

  async function handleSubmit(event) {
    event.preventDefault();

    try {
      const { user, session, error } = await supabase.auth.signUp({
        email,
        password: motDePasse,
      });

      if (error) {
        throw error;
      }

      console.log('Utilisateur inscrit avec succès:', user);

      // Enregistrement dans la table 'Connexion'
      const { data, error: dbError } = await supabase
        .from('Connexion')
        .insert({ email, mdp: motDePasse });

      if (dbError) {
        throw dbError;
      }

      console.log('Données enregistrées dans la table Connexion:', data);

      // Redirection ou affichage d'un message de succès
    } catch (error) {
      console.error('Erreur lors de l\'inscription:', error.message);
      setError(error.message);
    }
  }

  return (
    <div className="inscription">
      <h2>Inscription</h2>
      {error && <div className="error">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email :</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Mot de passe :</label>
          <input type="password" value={motDePasse} onChange={(e) => setMotDePasse(e.target.value)} />
        </div>
        <button type="submit">S'inscrire</button>
      </form>
    </div>
  );
}

export default Inscription;
