import React, { useState, useEffect } from 'react';
import supabase  from '../../supabase'; // Assurez-vous que le chemin est correct
import './FactureAffichage.css'; // Vérifiez le chemin d'accès au fichier CSS
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin est correct

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  // Récupération des factures depuis Supabase
  useEffect(() => {
    const fetchFactures = async () => {
      const { data: fetchedFactures, error } = await supabase.from('Facture').select('*');
      if (!error) {
        setFactures(fetchedFactures);
      } else {
        console.error('Erreur lors du chargement des factures:', error.message);
      }
    };

    fetchFactures();
  }, []);

  // Suppression d'une facture
  const handleDelete = async (id) => {
    const { error } = await supabase.from('Facture').delete().match({ id });
    if (!error) {
      setFactures(factures.filter(facture => facture.id !== id));
    } else {
      console.error('Erreur lors de la suppression de la facture:', error.message);
    }
  };

  // Gestion de la modification d'une facture
  const handleEdit = (id) => {
    // Vous devriez implémenter une logique pour ouvrir un formulaire de modification ici
    console.log('Modification de la facture avec l’id:', id);
    // Par exemple, définir l'état du formulaire à éditer ou ouvrir un modal de modification
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-content">
        <h1>Liste des Factures</h1>
        <div className="facture-list">
          {factures.map(facture => (
            <div key={facture.id} className="facture-item">
              <p>Client: {facture.Client}</p>
              {/* Ajoutez d'autres champs de facture ici comme nécessaire */}
              <button onClick={() => handleEdit(facture.id)}>Modifier</button>
              <button onClick={() => handleDelete(facture.id)}>Supprimer</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FactureAffichage;
