import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Mettez à jour le chemin selon votre structure
import supabase from '../../supabase';// Mettez à jour le chemin selon votre structure
import './FactureAffichage.css'; // Mettez à jour le chemin selon votre structure

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  // Supposons que vous avez un état pour gérer la facture en cours de modification
  const [editFactureId, setEditFactureId] = useState(null);

  useEffect(() => {
    const fetchFactures = async () => {
      const { data, error } = await supabase.from('Facture').select('*');
      if (!error) {
        setFactures(data);
      } else {
        console.error(error);
      }
    };

    fetchFactures();
  }, []);

  // Fonction pour démarrer la modification d'une facture
  const startEdit = (factureId) => {
    setEditFactureId(factureId);
    // Vous devrez également définir la facture actuelle comme état de modification
  };

  // Fonction pour gérer la soumission de la modification
  const handleEditSubmit = async (e, factureId) => {
    e.preventDefault();
    // Ici, vous devrez récupérer les données mises à jour du formulaire et les envoyer à la base de données
    // Ensuite, réinitialiser editFactureId et actualiser l'état des factures
  };

  // Fonction pour supprimer une facture
  const handleDelete = async (factureId) => {
    const { error } = await supabase.from('Facture').delete().match({ id: factureId });
    if (!error) {
      setFactures(factures.filter(facture => facture.id !== factureId));
    } else {
      console.error(error);
    }
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-affichage">
        {/* ... Votre code pour l'en-tête, les filtres, et les boutons ... */}
        <table className="facture-table">
          <thead>
            <tr>
              <th>N° facture</th>
              <th>Client</th>
              <th>Date</th>
              <th>Montant HT</th>
              <th>TVA</th>
              <th>Montant TTC</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {factures.map(facture => (
              <tr key={facture.id}>
                {/* ... Les cellules du tableau pour les données de la facture ... */}
                <td>
                  {editFactureId === facture.id ? (
                    // Formulaire de modification si l'ID correspond
                    <form onSubmit={(e) => handleEditSubmit(e, facture.id)}>
                      {/* Inputs pour la modification de la facture */}
                      <button type="submit">Sauvegarder</button>
                    </form>
                  ) : (
                    // Vue normale si l'ID ne correspond pas
                    <>
                      <button onClick={() => startEdit(facture.id)}>Modifier</button>
                      <button onClick={() => handleDelete(facture.id)}>Supprimer</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FactureAffichage;
