import React, { useEffect, useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import  './Profile.css'; // Votre fichier CSS pour ProfilePage


function ProfilePage() {
  const [profile, setProfile] = useState({
    nom: '', prenom: '', email: '', adresse: '', dateNaissance: '', telephone: ''
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      // Remplacez 'votre_id_utilisateur' par la logique d'obtention de l'ID utilisateur actuel
      const user = supabase.auth.user();
      if (user) {
        const { data, error } = await supabase
          .from('Authentification')
          .select('*')
          .eq('id', user.id)
          .single();

        if (error) {
          console.error('Erreur lors du chargement du profil', error);
          return;
        }

        setProfile(data || {});
      }
      setLoading(false);
    };

    fetchProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    // Logique de mise à jour du profil
    const { error } = await supabase
      .from('Authentification')
      .update(profile)
      .eq('id', profile.id); // Assurez-vous d'avoir un champ `id` ou utilisez l'ID de l'utilisateur

    if (error) {
      alert('Erreur lors de la mise à jour du profil');
      console.error(error);
    } else {
      alert('Profil mis à jour avec succès.');
    }
    setLoading(false);
  };

  if (loading) return <div>Chargement...</div>;

  return (
    <div className="profilePage">
      <Menu />
      <form onSubmit={handleSubmit} className="profileForm">
        <div className="formGroup">
          <label>Nom</label>
          <input type="text" name="nom" value={profile.nom || ''} onChange={handleInputChange} />
        </div>
        <div className="formGroup">
          <label>Prénom</label>
          <input type="text" name="prenom" value={profile.prenom || ''} onChange={handleInputChange} />
        </div>
        <div className="formGroup">
          <label>Email</label>
          <input type="email" name="email" value={profile.email || ''} onChange={handleInputChange} />
        </div>
        <div className="formGroup">
          <label>Adresse</label>
          <input type="text" name="adresse" value={profile.adresse || ''} onChange={handleInputChange} />
        </div>
        <div className="formGroup">
          <label>Date de Naissance</label>
          <input type="date" name="dateNaissance" value={profile.dateNaissance || ''} onChange={handleInputChange} />
        </div>
        <div className="formGroup">
          <label>Téléphone</label>
          <input type="text" name="telephone" value={profile.telephone || ''} onChange={handleInputChange} />
        </div>
        <button type="submit" disabled={loading}>Mettre à jour</button>
      </form>
    </div>
  );
}

export default ProfilePage;
