import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin est correct pour le Menu
import  supabase from '../../supabase'; // Assurez-vous que le chemin est correct pour Supabase
import './FactureAffichage.css'; // Assurez-vous que le chemin est correct pour le CSS

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  const [editFactureId, setEditFactureId] = useState(null);
  const [editFormData, setEditFormData] = useState({
    Client: '',
    DelaiDeReglement: '',
    DateEdition: '',
    Adresse: '',
    CodePostal: '',
    Ville: '',
    ComplementAdresse: '',
    Pays: '',
    Commentaire: '',
    TotalHT: '',
    TVA: '',
    TotalTTC: '',
  });

  useEffect(() => {
    async function fetchFactures() {
      const { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur lors du chargement des factures:', error);
      } else {
        setFactures(data);
      }
    }
    fetchFactures();
  }, []);

  const handleEditClick = (facture) => {
    setEditFactureId(facture.id);
    setEditFormData(facture);
  };

  const handleEditFormChange = (event) => {
    const { name, value } = event.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleEditFormSubmit = async (event) => {
    event.preventDefault();
    const { error } = await supabase
      .from('Facture')
      .update(editFormData)
      .match({ id: editFactureId });

    if (error) {
      console.error('Erreur lors de la mise Ã  jour de la facture:', error);
    } else {
      const updatedFactures = factures.map((facture) => {
        if (facture.id === editFactureId) return { ...facture, ...editFormData };
        return facture;
      });
      setFactures(updatedFactures);
      setEditFactureId(null);
    }
  };

  const handleCancelEdit = () => {
    setEditFactureId(null);
  };

  const handleDeleteClick = async (factureId) => {
    const { error } = await supabase
      .from('Facture')
      .delete()
      .match({ id: factureId });

    if (error) {
      console.error('Erreur lors de la suppression de la facture:', error);
    } else {
      setFactures(factures.filter((facture) => facture.id !== factureId));
    }
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-affichage">
        <h1>Gestion des Factures</h1>
        <table>
          <thead>
            <tr>
              <th>Client</th>
              <th>DÃ©lai de RÃ¨glement</th>
              <th>Date d'Ãdition</th>
              <th>Adresse</th>
              <th>Code Postal</th>
              <th>Ville</th>
              <th>ComplÃ©ment d'Adresse</th>
              <th>Pays</th>
              <th>Commentaire</th>
              <th>Total HT</th>
              <th>TVA (%)</th>
              <th>Total TTC</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {factures.map((facture) => (
              <tr key={facture.id}>
                {/* Affichage des informations de la facture */}
                {/* InsÃ©rez ici la logique pour basculer entre l'affichage et le formulaire d'Ã©dition */}
                {/* Utilisez handleEditClick pour passer en mode Ã©dition et handleDeleteClick pour supprimer une entrÃ©e */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FactureAffichage;
