// Importations nécessaires
import React, { useState, useEffect } from 'react';
import './Menu.css'; // Votre CSS pour le layout et le menu
import logo from '../images/InvoicePro.png'; // Assurez-vous que le chemin vers votre logo est correct
import { FaBars } from 'react-icons/fa';
import supabase from '../../supabase'; // Vérifiez le chemin d'accès à votre client Supabase

const App = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [formData, setFormData] = useState({
    Client: '',
    DelaiDeReglement: '',
    DateEdition: '',
    Adresse: '',
    CodePostal: '',
    Ville: '',
    ComplementAdresse: '',
    Pays: '',
    Commentaire: '',
    TotalHT: '',
    TVA: 20, // La TVA est fixée à 20%
    TotalTTC: '',
  });

  // Toggle pour le menu responsive
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // Mettez à jour TotalTTC à chaque fois que TotalHT ou TVA change
  useEffect(() => {
    const tvaValue = formData.TVA / 100;
    const totalHT = parseFloat(formData.TotalHT);
    const totalTTC = totalHT + (totalHT * tvaValue);
    if (!isNaN(totalTTC)) {
      setFormData(prevState => ({ ...prevState, TotalTTC: totalTTC.toFixed(2) }));
    }
  }, [formData.TotalHT, formData.TVA]);

  // Gérer les changements dans le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  // Soumettre le formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    const { error } = await supabase
      .from('Facture')
      .insert([formData]);

    if (error) alert(error.message);
    else {
      alert('Facture enregistrée avec succès !');
      // Réinitialiser le formulaire ici si nécessaire
    }
  };

  return (
    <div className="app">
      <button className="hamburger-menu" onClick={toggleSidebar}>
        <FaBars />
      </button>

      <aside className={`sidebar ${isSidebarOpen ? 'active' : ''}`}>
        <div className="logo-container">
          <img src={logo} alt="Logo" className="logo" />
        </div>
        <nav className="navigation">
          <ul className="nav-links">
            <li><a href="#" className="nav-link active"><FaBars /> Facture</a></li>
            {/* Ajoutez d'autres liens de navigation si nécessaire */}
          </ul>
        </nav>
        <div className="sidebar-footer">
          <a href="/profiles" className="footer-link"><FaBars /> Profile</a>
          {/* Ajoutez d'autres liens de pied de page si nécessaire */}
        </div>
      </aside>

      <div className="content">
        <form onSubmit={handleSubmit} className="facture-form">
          {/* Formulaire de facturation ici */}
          {/* Exemple d'un champ du formulaire */}
          <input type="text" name="Client" value={formData.Client} onChange={handleChange} placeholder="Client" required />
          {/* Continuez avec les autres champs du formulaire de facturation */}
          <button type="submit">Soumettre</button>
        </form>
      </div>
    </div>
  );
};

export default App;
