import React, { useEffect, useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import  './Profile.css'; // Votre fichier CSS pour ProfilePage






function ProfilePage() {
    const [profile, setProfile] = useState({
        nom: '', prenom: '', email: '', adresse: '', dateNaissance: '', telephone: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const userId = 2; // Exemple d'ID utilisateur

    useEffect(() => {
        const fetchProfile = async () => {
            const { data, error } = await supabase
                .from('Authentification')
                .select('*')
                .eq('id', userId)
                .single();

            if (error) {
                console.error('Erreur lors du chargement du profil:', error);
                return;
            }

            setProfile(data);
        };

        fetchProfile();
    }, [userId]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfile(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { error } = await supabase
            .from('Authentification')
            .update(profile)
            .eq('id', userId);

        if (error) {
            alert('Erreur lors de la mise à jour:', error.message);
        } else {
            alert('Profil mis à jour avec succès.');
            setIsEditing(false);
        }
    };

    return (
        <>
             <div className="app">
                 <Menu />
            <div className="profile-container">
                {!isEditing ? (
                    <>
                        <div className="profile-info">
                            <h2>Profil Utilisateur</h2>
                            <p>Nom: {profile.nom}</p>
                            <p>Prénom: {profile.prenom}</p>
                            <p>Email: {profile.email}</p>
                            <p>Adresse: {profile.adresse}</p>
                            <p>Date de Naissance: {profile.dateNaissance}</p>
                            <p>Téléphone: {profile.telephone}</p>
                        </div>
                        <button onClick={() => setIsEditing(true)}>Modifier le Profil</button>
                    </>
                ) : (
                    <form onSubmit={handleSubmit} className="profile-form">
                        <div className="form-group">
                            <label>Nom</label>
                            <input type="text" name="nom" value={profile.nom} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label>Prénom</label>
                            <input type="text" name="prenom" value={profile.prenom} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label>Email</label>
                            <input type="email" name="email" value={profile.email} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label>Adresse</label>
                            <input type="text" name="adresse" value={profile.adresse} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label>Date de Naissance</label>
                            <input type="date" name="dateNaissance" value={profile.dateNaissance} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label>Téléphone</label>
                            <input type="tel" name="telephone" value={profile.telephone} onChange={handleInputChange} />
                        </div>
                        <button type="submit">Sauvegarder les Changements</button>
                        <button type="button" onClick={() => setIsEditing(false)}>Annuler</button>
                    </form>
                )}
            </div>
            </div>
        </>
    );
}

export default ProfilePage;
