import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  // Charger les factures depuis Supabase
  useEffect(() => {
    const fetchFactures = async () => {
      let { data: factures, error } = await supabase
        .from('Facture')
        .select('*');
      if (error) console.error('Erreur lors de la récupération des factures', error);
      else setFactures(factures);
    };

    fetchFactures();
  }, []);

  // Supprimer une facture
  const handleDelete = async (id) => {
    const { data, error } = await supabase
      .from('Facture')
      .delete()
      .match({ id });
    if (error) console.error('Erreur lors de la suppression', error);
    else setFactures(factures.filter(facture => facture.id !== id));
  };

  return (
    <div>
      <h1>Factures</h1>
      {factures.map(facture => (
        <div key={facture.id}>
          <p>Client: {facture.Client}</p>
          <p>Délai de Règlement: {facture.DelaiDeReglement}</p>
          <p>Date d'Édition: {facture.DateEdition}</p>
          {/* Affichez plus de détails de la facture ici */}
          <p>Total TTC: {facture.TotalTTC}</p>
          <button onClick={() => handleDelete(facture.id)}>Supprimer</button>
          {/* Ajouter un bouton ou une action pour modifier ici */}
        </div>
      ))}
    </div>
  );
};

export default FactureAffichage;
