import React, { useState } from 'react';
import supabase from '../../supabase'; // Assurez-vous que le chemin est correct

function Inscription() {
  const [email, setEmail] = useState('');
  const [mdp, setMdp] = useState('');
  const [erreur, setErreur] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { user, error } = await supabase.auth.signUp({
        email,
        password: mdp,
      }, {
        // Désactiver l'envoi de l'e-mail de vérification
        sendVerificationEmail: true,
      });

      if (error) {
        throw error;
      }

      console.log('Utilisateur inscrit:', user);
      // Rediriger l'utilisateur vers une autre page ou afficher un message de succès
    } catch (error) {
      console.error('Erreur lors de l\'inscription:', error.message);
      setErreur(error.message);
    }
  };

  return (
    <div>
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="mdp">Mot de passe:</label>
          <input
            type="password"
            id="mdp"
            value={mdp}
            onChange={(e) => setMdp(e.target.value)}
          />
        </div>
        <button type="submit">S'inscrire</button>
      </form>
      {erreur && <div style={{ color: 'red' }}>{erreur}</div>}
    </div>
  );
}

export default Inscription;
