import React, { useState, useEffect } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu';
import './Profile.css';

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    Nom: '',
    Prénom: '',
    email: '',
    DateNaissance: '',
    Téléphone: '',
    Adresse: '',
  });

  useEffect(() => {
    const fetchUserData = async () => {
      // Supposons que vous connaissez l'email de l'utilisateur ou une autre clé unique pour la récupération des données
      const userEmail = 'user@example.com';

      const { data, error } = await supabase
        .from('Authentification')
        .select('*')
        .eq('email', userEmail) // Remplacez 'email' par la clé unique appropriée
        .single();

      if (error) {
        console.error('Erreur lors de la récupération des données utilisateur:', error);
      } else {
        setProfile(data);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="app-container">
      <Menu />
      <div className="profile-page-container">
        <h1>Mon Compte</h1>
        <h2>Mes Informations</h2>
        <div className="profile-information">
          <p><strong>Nom:</strong> {profile.Nom}</p>
          <p><strong>Prénom:</strong> {profile.Prénom}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Date de Naissance:</strong> {profile.DateNaissance}</p>
          <p><strong>Téléphone:</strong> {profile.Téléphone}</p>
          <p><strong>Adresse:</strong> {profile.Adresse}</p>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
