import React, { useState } from 'react';
import  supabase  from '../../supabase'; // Assurez-vous que le chemin est correct

const Connexion = () => {
  const [email, setEmail] = useState('');
  const [mdp, setMdp] = useState('');
  const [erreur, setErreur] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { user, error } = await supabase.auth.signIn({
        email,
        password: mdp,
      });

      if (error) {
        throw error;
      }

      // Rediriger l'utilisateur vers la page Menu en cas de succès
      window.location.href = '/menu';
    } catch (error) {
      console.error('Erreur lors de la connexion:', error.message);
      setErreur(error.message);
    }
  };

  return (
    <div className="login-page">
      <div className="form-section">
        <div className="form-container">
          <h1 className="form-title">Connexion</h1>
          <form className="login-form" onSubmit={handleSubmit}>
            <div className="input-group">
              <input type="email" placeholder="Adresse mail" required value={email} onChange={(e) => setEmail(e.target.value)} />
            </div>
            <div className="input-group">
              <input type="password" placeholder="Mot de passe" required value={mdp} onChange={(e) => setMdp(e.target.value)} />
            </div>
            {erreur && <p style={{ color: 'red' }}>{erreur}</p>}
            <div className="action-group">
              <button type="submit" className="submit-btn">Je me connecte</button>
              <p className="alternative-action">Pas encore de compte ? <a href="/inscriptions" className="link">S'inscrire</a></p>
            </div>
          </form>
        </div>
      </div>
      <div className="background-image-section"></div>
    </div>
  );
};

export default Connexion;
