import React, { useEffect, useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import styles from  './Profile.css'; // Votre fichier CSS pour ProfilePage

function ProfilePage() {
  const [profile, setProfile] = useState({
      nom: '', prenom: '', email: '', adresse: '', dateNaissance: '', telephone: ''
  });
  const [loading, setLoading] = useState(true);

  // Utilisez un identifiant prédéfini ou passé via un contexte / routeur
  const profileId = 'votre_identifiant_unique';

  useEffect(() => {
      fetchProfile();
  }, []);

  const fetchProfile = async () => {
      let { data, error } = await supabase
          .from('Authentification')
          .select('*')
          .eq('id', profileId) // Utilisez un identifiant spécifique pour la requête
          .single();
      if (error) {
          console.error('Erreur lors du chargement du profil', error);
          return;
      }
      setProfile({ ...data });
      setLoading(false);
  };

  const handleSubmit = async (e) => {
      e.preventDefault();
      setLoading(true);
      const { error } = await supabase
          .from('Authentification')
          .update({ ...profile })
          .eq('id', profileId); // Utilisez le même identifiant pour la mise à jour
      if (error) {
          alert('Erreur lors de la mise à jour', error.message);
      } else {
          alert('Profil mis à jour avec succès');
      }
      setLoading(false);
  };

  const handleChange = (e) => {
      setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  return (
      <div className="profilePage">
          <Menu />
          <form onSubmit={handleSubmit} className="profileForm">
              {/* Les champs du formulaire */}
              <button type="submit" disabled={loading} className="submitButton">
                  Mettre à jour
              </button>
          </form>
      </div>
  );
}

export default ProfilePage;
