import React, { useState, useEffect } from 'react';
import supabase from '../../supabase';

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  const fetchFactures = async () => {
    const { data, error } = await supabase.from('Facture').select('*');
    if (error) console.log('Erreur:', error.message);
    else setFactures(data);
  };

  useEffect(() => {
    fetchFactures();
  }, []);

  return (
    <div>
      <h1>Factures</h1>
      {factures.map((facture) => (
        <div key={facture.id}>
          <p>Client: {facture.Client}</p>
          {/* Affichez d'autres d√©tails ici */}
          <button onClick={() => deleteFacture(facture.id)}>Supprimer</button>
          {/* Bouton ou lien vers le formulaire de modification ici */}
        </div>
      ))}
    </div>
  );
};

export default FactureAffichage;
