import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient'; // Ajustez ce chemin selon votre configuration
import './FactureAffichage.css'; // Assurez-vous d'inclure le chemin correct pour vos styles CSS

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  const [editFactureId, setEditFactureId] = useState(null);
  const [formData, setFormData] = useState({
    Client: '',
    TotalHT: '',
    TVA: '',
    TotalTTC: '',
  });

  useEffect(() => {
    fetchFactures();
  }, []);

  async function fetchFactures() {
    const { data, error } = await supabase.from('Facture').select('*');
    if (!error) {
      setFactures(data);
    } else {
      console.error('Erreur lors du chargement des factures', error.message);
    }
  }

  const handleEdit = (facture) => {
    setEditFactureId(facture.id);
    setFormData({
      Client: facture.Client,
      TotalHT: facture.TotalHT || '',
      TVA: facture.TVA || '',
      TotalTTC: facture.TotalTTC || '',
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let newFormData = { ...formData, [name]: value };

    // Condition spécifique pour recalculer TotalTTC uniquement si TotalHT ou TVA est modifié
    if (name === 'TotalHT' || name === 'TVA') {
      const totalHT = parseFloat(formData.TotalHT) || 0;
      const tva = parseFloat(formData.TVA) / 100 || 0;
      newFormData.TotalTTC = (totalHT + totalHT * tva).toFixed(2);
    }

    setFormData(newFormData);
  };

  const handleSave = async () => {
    const { error } = await supabase
      .from('Facture')
      .update({
        Client: formData.Client,
        TotalHT: parseFloat(formData.TotalHT),
        TVA: parseFloat(formData.TVA),
        TotalTTC: parseFloat(formData.TotalTTC),
      })
      .match({ id: editFactureId });
    if (!error) {
      fetchFactures();
      setEditFactureId(null);
    } else {
      console.error('Erreur lors de la mise à jour', error.message);
    }
  };

  const handleCancel = () => {
    setEditFactureId(null);
  };

  const handleDelete = async (id) => {
    const { error } = await supabase.from('Facture').delete().match({ id });
    if (!error) {
      fetchFactures();
    } else {
      console.error('Erreur lors de la suppression', error.message);
    }
  };

  return (
    <div className="app">
      <h1>Gestion des Factures</h1>
      <table>
        <thead>
          <tr>
            <th>Client</th>
            <th>Total HT</th>
            <th>TVA (%)</th>
            <th>Total TTC</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {factures.map((facture) => (
            <tr key={facture.id}>
              <td>
                {editFactureId === facture.id ? (
                  <input
                    type="text"
                    name="Client"
                    value={formData.Client}
                    onChange={handleChange}
                  />
                ) : (
                  facture.Client
                )}
              </td>
              <td>
                {editFactureId === facture.id ? (
                  <input
                    type="number"
                    name="TotalHT"
                    value={formData.TotalHT}
                    onChange={handleChange}
                  />
                ) : (
                  facture.TotalHT
                )}
              </td>
              <td>
                {editFactureId === facture.id ? (
                  <input
                    type="number"
                    name="TVA"
                    value={formData.TVA}
                    onChange={handleChange}
                  />
                ) : (
                  facture.TVA
                )}
              </td>
              <td>{facture.TotalTTC}</td>
              <td>
                {editFactureId === facture.id ? (
                  <>
                    <button onClick={handleSave}>Save</button>
                    <button onClick={handleCancel}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => handleEdit(facture)}>Edit</button>
                    <button onClick={() => handleDelete(facture.id)}>Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FactureAffichage;
