import React, { useState, useEffect } from 'react';
import supabase from '../../supabase';



const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  const [editingFacture, setEditingFacture] = useState(null);

  // Charger les factures depuis Supabase à l'initialisation du composant
  useEffect(() => {
    const fetchFactures = async () => {
      let { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur lors de la récupération des factures:', error);
      } else {
        setFactures(data);
      }
    };

    fetchFactures();
  }, []);

  // Supprimer une facture
  const handleDelete = async (id) => {
    const { error } = await supabase.from('Facture').delete().match({ id });
    if (error) {
      console.error('Erreur lors de la suppression:', error);
    } else {
      setFactures(factures.filter(facture => facture.id !== id));
    }
  };

  // Mettre à jour l'état lors de la modification des champs du formulaire
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingFacture({ ...editingFacture, [name]: value });
  };

  // Soumettre les modifications d'une facture
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const { error } = await supabase
      .from('Facture')
      .update({
        Client: editingFacture.Client,
        DelaiDeReglement: editingFacture.DelaiDeReglement,
        DateEdition: editingFacture.DateEdition,
        Adresse: editingFacture.Adresse,
        CodePostal: editingFacture.CodePostal,
        Ville: editingFacture.Ville,
        ComplementAdresse: editingFacture.ComplementAdresse,
        Pays: editingFacture.Pays,
        Commentaire: editingFacture.Commentaire,
        TotalHT: editingFacture.TotalHT,
        TotalTTC: editingFacture.TotalTTC,
      })
      .match({ id: editingFacture.id });

    if (error) {
      console.error('Erreur lors de la mise à jour:', error);
    } else {
      const updatedFactures = factures.map(facture => {
        if (facture.id === editingFacture.id) return { ...editingFacture };
        return facture;
      });
      setFactures(updatedFactures);
      setEditingFacture(null); // Quitter le mode édition
    }
  };

  return (
    <div>
      <h1>Liste des Factures</h1>
      {editingFacture ? (
        <form onSubmit={handleEditSubmit}>
          <input type="text" name="Client" value={editingFacture.Client || ''} onChange={handleEditChange} placeholder="Client" />
          <input type="date" name="DelaiDeReglement" value={editingFacture.DelaiDeReglement || ''} onChange={handleEditChange} placeholder="Délai de Règlement" />
          <input type="date" name="DateEdition" value={editingFacture.DateEdition || ''} onChange={handleEditChange} placeholder="Date d'Édition" />
          <input type="text" name="Adresse" value={editingFacture.Adresse || ''} onChange={handleEditChange} placeholder="Adresse" />
          <input type="text" name="CodePostal" value={editingFacture.CodePostal || ''} onChange={handleEditChange} placeholder="Code Postal" />
          <input type="text" name="Ville" value={editingFacture.Ville || ''} onChange={handleEditChange} placeholder="Ville" />
          <input type="text" name="ComplementAdresse" value={editingFacture.ComplementAdresse || ''} onChange={handleEditChange} placeholder="Complément d'Adresse" />
          <input type="text" name="Pays" value={editingFacture.Pays || ''} onChange={handleEditChange} placeholder="Pays" />
          <textarea name="Commentaire" value={editingFacture.Commentaire || ''} onChange={handleEditChange} placeholder="Commentaire" />
          <input type="number" name="TotalHT" value={editingFacture.TotalHT || ''} onChange={handleEditChange} placeholder="Total HT" />
          <input type="number" name="TotalTTC" value={editingFacture.TotalTTC || ''} onChange={handleEditChange} placeholder="Total TTC" />
          <button type="submit">Enregistrer</button>
          <button type="button" onClick={() => setEditingFacture(null)}>Annuler</button>
        </form>
      ) : (
        factures.map((facture) => (
          <div key={facture.id} style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ccc' }}>
            <p>Client: {facture.Client}</p>
            <p>Délai de Règlement: {facture.DelaiDeReglement}</p>
            <p>Date d'Édition: {facture.DateEdition}</p>
            <p>Adresse: {facture.Adresse}</p>
            <p>Code Postal: {facture.CodePostal}</p>
            <p>Ville: {facture.Ville}</p>
            <p>Complément d'Adresse: {facture.ComplementAdresse || 'N/A'}</p>
            <p>Pays: {facture.Pays}</p>
            <p>Commentaire: {facture.Commentaire || 'N/A'}</p>
            <p>Total HT: {facture.TotalHT}</p>
            <p>Total TTC: {facture.TotalTTC}</p>
            <button onClick={() => setEditingFacture(facture)}>Modifier</button>
            <button onClick={() => handleDelete(facture.id)}>Supprimer</button>
          </div>
        ))
      )}
    </div>
  );
};

export default FactureAffichage;
