// Facture.jsx
import React, { useState } from 'react';
import Menu from './Menu'; // Assurez-vous que le chemin d'importation est correct
import { FaBars } from 'react-icons/fa';
import supabase from '../../supabase'; // Ajustez le chemin d'accès selon votre configuration

const Facture = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Logique pour gérer l'état du formulaire de la facture ici...
  // Exemple simplifié :
  const [formData, setFormData] = useState({
    client: '',
    // Ajoutez d'autres champs nécessaires
  });

  // Fonction pour gérer le changement de l'état du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Fonction pour gérer la soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Logique pour soumettre les données à Supabase ou votre backend
    alert('Facture soumise');
  };

  return (
    <div className="app">
      <button className="hamburger-menu" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
        <FaBars />
      </button>
      <Menu /> {/* Ici, le menu est toujours visible, mais vous pouvez le rendre conditionnel si nécessaire */}
      <div className="content">
        <form onSubmit={handleSubmit} className="facture-form">
          {/* Exemple de champ du formulaire */}
          <label>
            Client:
            <input
              type="text"
              name="client"
              value={formData.client}
              onChange={handleChange}
            />
          </label>
          {/* Ajoutez d'autres champs nécessaires ici */}
          <button type="submit">Soumettre la facture</button>
        </form>
      </div>
    </div>
  );
};

export default Facture;
