import React, { useState } from 'react';
import Menu from '../Menu/Menu';// Assurez-vous que le chemin est correct
import './Facture.css'; // Vérifiez le chemin d'accès au fichier CSS

const Facture = () => {
  const [formData, setFormData] = useState({
    client: '',
    delaiDeReglement: '',
    dateEdition: '',
    adresse: '',
    codePostal: '',
    ville: '',
    complementAdresse: '',
    pays: '',
    commentaire: '',
    totalHT: '',
    TVA: 20,
    totalTTC: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Facture soumise', formData);
    alert('Facture enregistrée avec succès !');
  };

  return (
    <div className="app">
    <Menu />
    <div className="facture-content">
        <h1>Créer une nouvelle facture</h1>
        <form onSubmit={handleSubmit} className="facture-form">
          <input type="text" name="client" value={formData.client} onChange={handleChange} placeholder="Client" required />
          <input type="date" name="delaiDeReglement" value={formData.delaiDeReglement} onChange={handleChange} placeholder="Délai de règlement" required />
          <input type="date" name="dateEdition" value={formData.dateEdition} onChange={handleChange} placeholder="Date d'édition" required />
          <input type="text" name="adresse" value={formData.adresse} onChange={handleChange} placeholder="Adresse" required />
          <input type="text" name="codePostal" value={formData.codePostal} onChange={handleChange} placeholder="Code postal" required />
          <input type="text" name="ville" value={formData.ville} onChange={handleChange} placeholder="Ville" required />
          <input type="text" name="complementAdresse" value={formData.complementAdresse} onChange={handleChange} placeholder="Complément d'adresse" />
          <input type="text" name="pays" value={formData.pays} onChange={handleChange} placeholder="Pays" required />
          <textarea name="commentaire" value={formData.commentaire} onChange={handleChange} placeholder="Commentaire"></textarea>
          <input type="number" name="totalHT" value={formData.totalHT} onChange={handleChange} placeholder="Total HT" required />
          <input type="text" name="TVA" value={formData.TVA} onChange={handleChange} placeholder="TVA" readOnly />
          <input type="text" name="totalTTC" value={formData.totalTTC} onChange={handleChange} placeholder="Total TTC" readOnly />
          <button type="submit">Soumettre la facture</button>
        </form>
      </div>
    </div>
  );
};

export default Facture;
