import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin est correct
import supabase from '../../supabase';
import './Facture.css'; // Vérifiez le chemin d'accès au fichier CSS

const Facture = () => {
  const [formData, setFormData] = useState({
    client: '',
    delaiDeReglement: '',
    dateEdition: '',
    adresse: '',
    codePostal: '',
    ville: '',
    complementAdresse: '',
    pays: '',
    commentaire: '',
    totalHT: 0,
    TVA: 20, // La TVA est fixée à 20%
    totalTTC: 0,
  });

  useEffect(() => {
    // Calcul automatique du Total TTC chaque fois que Total HT ou TVA change
    const totalHT = parseFloat(formData.totalHT);
    const tva = parseFloat(formData.TVA) / 100;
    const totalTTC = totalHT + totalHT * tva;
    setFormData(formData => ({ ...formData, totalTTC: totalTTC.toFixed(2) }));
  }, [formData.totalHT, formData.TVA]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase
      .from('Facture')
      .insert([formData]);

    if (error) {
      alert(`Erreur lors de l'enregistrement : ${error.message}`);
    } else {
      alert('Facture enregistrée avec succès !');
      console.log('Données insérées :', data);
      // Ici, vous pouvez réinitialiser le formulaire ou effectuer d'autres actions à la réussite
    }
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-content">
        <h1>Créer une nouvelle facture</h1>
        <form onSubmit={handleSubmit} className="facture-form">
          <input type="text" name="client" value={formData.client} onChange={handleChange} placeholder="Client" required />
          <input type="date" name="delaiDeReglement" value={formData.delaiDeReglement} onChange={handleChange} required />
          <input type="date" name="dateEdition" value={formData.dateEdition} onChange={handleChange} required />
          <input type="text" name="adresse" value={formData.adresse} onChange={handleChange} required />
          <input type="text" name="codePostal" value={formData.codePostal} onChange={handleChange} required />
          <input type="text" name="ville" value={formData.ville} onChange={handleChange} required />
          <input type="text" name="complementAdresse" value={formData.complementAdresse} onChange={handleChange} />
          <input type="text" name="pays" value={formData.pays} onChange={handleChange} required />
          <textarea name="commentaire" value={formData.commentaire} onChange={handleChange} required></textarea>
          <input type="number" name="totalHT" value={formData.totalHT} onChange={handleChange} required />
          {/* La TVA et Total TTC sont calculés automatiquement, donc pas de champ pour modifier la TVA directement */}
          <input type="text" name="totalTTC" value={formData.totalTTC} readOnly />
          <button type="submit">Soumettre la facture</button>
        </form>
      </div>
    </div>
  );
};

export default Facture;
