import React, { useEffect, useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import  './Profile.css'; // Votre fichier CSS pour ProfilePage




function ProfilePage() {
    const [profile, setProfile] = useState({
        nom: '', prenom: '', email: '', adresse: '', dateNaissance: '', telephone: ''
    });
    const [loading, setLoading] = useState(true);
    const [isEditing, setIsEditing] = useState(false);
    const userId = 'identifiant-unique-de-l-utilisateur'; // Assurez-vous que ce soit sécurisé

    useEffect(() => {
        const fetchProfile = async () => {
            setLoading(true);
            const { data, error } = await supabase
                .from('Authentification')
                .select('*')
                .eq('id', userId)
                .single();

            if (error) {
                console.error('Erreur lors du chargement du profil:', error);
                setLoading(false);
                return;
            }

            setProfile(data);
            setLoading(false);
        };

        fetchProfile();
    }, [userId]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setProfile(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        const { error } = await supabase
            .from('Authentification')
            .update({ ...profile })
            .eq('id', userId);

        if (error) {
            alert('Erreur lors de la mise à jour:', error.message);
        } else {
            alert('Profil mis à jour avec succès');
        }
        setLoading(false);
        setIsEditing(false); // Ferme le formulaire de modification après la mise à jour
    };

    if (loading) return <div>Chargement...</div>;

    return (
        <>
            <Menu />
            <div className="profilePage">
                {!isEditing ? (
                    <div>
                        <h2>Informations de Profil</h2>
                        <p>Nom: {profile.nom}</p>
                        <p>Prénom: {profile.prenom}</p>
                        <p>Email: {profile.email}</p>
                        <p>Adresse: {profile.adresse}</p>
                        <p>Date de Naissance: {profile.dateNaissance}</p>
                        <p>Téléphone: {profile.telephone}</p>
                        <button onClick={() => setIsEditing(true)}>Modifier</button>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} className="profileForm">
                        {/* Formulaire de modification avec les mêmes champs */}
                        <button type="submit">Sauvegarder</button>
                        <button type="button" onClick={() => setIsEditing(false)}>Annuler</button>
                    </form>
                )}
            </div>
        </>
    );
}

export default ProfilePage;
