import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin est correct
import supabase from '../../supabase'; // Vérifiez le chemin d'accès à votre client Supabase
import './FactureAffichage.css'; // Le CSS doit être créé pour correspondre au design

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  useEffect(() => {
    // Vous devrez charger les factures de votre base de données ici
    const chargerFactures = async () => {
      let { data, error } = await supabase
        .from('Facture')
        .select('*');
      if (error) {
        console.log('Erreur lors du chargement des factures', error);
      } else {
        setFactures(data);
      }
    };

    chargerFactures();
  }, []);

  // Fonctions pour gérer la modification et la suppression de factures
  const handleEdit = (id) => {
    // Logique pour éditer une facture
  };

  const handleDelete = async (id) => {
    const { data, error } = await supabase
      .from('Facture')
      .delete()
      .match({ id });

    if (error) {
      console.log('Erreur lors de la suppression', error);
    } else {
      // Mise à jour de l'état pour refléter la suppression
      setFactures(factures.filter(facture => facture.id !== id));
    }
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-affichage">
        {/* Ici vous ajoutez vos en-têtes, filtres et boutons, comme Imprimer, Exporter, Ajouter */}
        <div className="facture-operations">
          {/* Boutons et filtres */}
        </div>

        {/* Liste des factures */}
        <div className="facture-liste">
          {factures.map(facture => (
            <div key={facture.id} className="facture-item">
              {/* Détails de la facture ici */}
              <div>{facture.Client}</div>
              <div>{facture.DateEdition}</div>
              <div>{facture.TotalHT}</div>
              <div>{facture.TVA}</div>
              <div>{facture.TotalTTC}</div>
              {/* Boutons pour modifier et supprimer */}
              <button onClick={() => handleEdit(facture.id)}>Modifier</button>
              <button onClick={() => handleDelete(facture.id)}>Supprimer</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FactureAffichage;
