import React, { useState, useEffect } from 'react';
import supabase from '../../supabase'; // Assurez-vous que le chemin est correct

function App() {
  const [isConnected, setIsConnected] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    checkConnection();
  }, []);

  async function checkConnection() {
    try {
      const { data, error } = await supabase
        .from('Connexion') // Utilisez une table existante pour vérifier la connexion
        .select();

      if (error) {
        throw error;
      }

      console.log(data);
      setUserData(data); // Stocker les données récupérées dans l'état local
      setIsConnected(true);
    } catch (error) {
      console.error('Erreur lors de la connexion à Supabase:', error.message);
      setIsConnected(false);
    }
  }

  return (
    <div className="App">
      {isConnected ? (
        <>
          <h1>Connexion à Supabase réussie!</h1>
          {userData && userData.length > 0 ? ( // Vérifier si userData contient des données
            <div>
              <h2>Email de la BDD:</h2>
              <p>{userData[0].mdp}</p> {/* Supposant que le champ email existe dans votre table */}
            </div>
          ) : (
            <p>Aucune donnée disponible</p>
          )}
        </>
      ) : (
        <h1>Échec de la connexion à Supabase.</h1>
      )}
    </div>
  );
}

export default App;
