import React, { useState } from 'react';
import supabase from '../../supabase';
import './Profile.css';

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    Nom: '',
    Prénom: '',
    email: '',
    DateNaissance: '', // Assurez-vous que cela correspond au format attendu par la BDD
    Téléphone: '', // Renommé pour correspondre au nom de la colonne dans la BDD
    Adresse: '',
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Conversion de la date de naissance au format timestamp si nécessaire
    const dateNaissanceTimestamp = new Date(profile.DateNaissance).getTime();

    // Conversion du numéro de téléphone en nombre si nécessaire
    const telephoneNumber = parseInt(profile.Téléphone, 10);

    const { data, error } = await supabase
      .from('Authentification') // Assurez-vous que c'est le nom correct de votre table
      .update({
        Nom: profile.Nom,
        Prénom: profile.Prénom,
        // Ne mettez pas à jour l'email si c'est un identifiant unique
        DateNaissance: dateNaissanceTimestamp,
        Téléphone: telephoneNumber,
        Adresse: profile.Adresse,
      })
      .match({ email: profile.email }); // Utilisez match pour une correspondance exacte

    if (error) {
      console.error('Erreur lors de la mise à jour:', error);
    } else {
      console.log('Profil mis à jour:', data);
      // Vous pouvez ici actualiser l'état ou afficher un message à l'utilisateur
    }
  };

  return (
    <div className="profile-page-container">
      <h1>Mon Compte</h1>
      <h2>Mes Informations</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input
            type="text"
            name="Nom"
            value={profile.Nom}
            onChange={handleChange}
          />
        </label>
        <label>
          Prénom:
          <input
            type="text"
            name="Prénom"
            value={profile.Prénom}
            onChange={handleChange}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
          />
        </label>
        <label>
          Date de naissance:
          <input
            type="date"
            name="DateNaissance"
            value={profile.DateNaissance}
            onChange={handleChange}
          />
        </label>
        <label>
          Téléphone:
          <input
            type="tel"
            name="Téléphone"
            value={profile.Téléphone}
            onChange={handleChange}
          />
        </label>
        <label>
          Adresse:
          <input
            type="text"
            name="Adresse"
            value={profile.Adresse}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Enregistrer mes informations</button>
      </form>
    </div>
  );
};

export default ProfilePage;
