// ProfilePage.jsx
import React, { useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import './Profile.css'; // Votre fichier CSS pour ProfilePage

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    Nom: '',
    Prénom: '',
    email: '',
    DateNaissance: '',
    Téléphone: '',
    Adresse: '',
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Conversion des données si nécessaire
    // Assurez-vous que ces conversions correspondent à ce que votre BDD attend
    const dateNaissanceTimestamp = new Date(profile.DateNaissance).getTime();
    const telephoneNumber = parseInt(profile.Téléphone, 10);

    // Mise à jour du profil dans la BDD Supabase
    const { data, error } = await supabase
      .from('Authentification')
      .update({
        Nom: profile.Nom,
        Prénom: profile.Prénom,
        DateNaissance: dateNaissanceTimestamp,
        Téléphone: telephoneNumber,
        Adresse: profile.Adresse,
      })
      .match({ email: profile.email });

    if (error) {
      console.error('Erreur lors de la mise à jour:', error);
    } else {
      console.log('Profil mis à jour:', data);
      // Vous pouvez également définir ici une logique pour informer l'utilisateur de la mise à jour réussie
    }
  };

  return (
    <>
     <div className="app">
      <Menu />
      <div className="profile-page-container">
        <h1>Mon Compte</h1>
        <h2>Mes Informations</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Nom:
            <input
              type="text"
              name="Nom"
              value={profile.Nom}
              onChange={handleChange}
            />
          </label>
          <label>
            Prénom:
            <input
              type="text"
              name="Prénom"
              value={profile.Prénom}
              onChange={handleChange}
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={profile.email}
              onChange={handleChange}
              readOnly // Rendre l'email en lecture seule si c'est un identifiant
            />
          </label>
          <label>
            Date de naissance:
            <input
              type="date"
              name="DateNaissance"
              value={profile.DateNaissance}
              onChange={handleChange}
            />
          </label>
          <label>
            Téléphone:
            <input
              type="tel"
              name="Téléphone"
              value={profile.Téléphone}
              onChange={handleChange}
            />
          </label>
          <label>
            Adresse:
            <input
              type="text"
              name="Adresse"
              value={profile.Adresse}
              onChange={handleChange}
            />
          </label>
          <button type="submit">Enregistrer mes informations</button>
        </form>
      </div>
      </div>
    </>
  );
};

export default ProfilePage;
