import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabase'; // Vérifiez le chemin d'accès à votre client Supabase
import './FactureAffichage.css'; // Vérifiez le chemin d'accès au fichier CSS

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  useEffect(() => {
    const fetchFactures = async () => {
      const { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur lors du chargement des factures:', error);
      } else {
        setFactures(data);
      }
    };

    fetchFactures();
  }, []);

  return (
    <div className="facture-affichage">
      <h1>MA COMPTABILITÉ - GESTION DES FACTURES!</h1>
      {/* Insert here your filters and buttons like 'Imprimer', 'Exporter', and 'Ajouter' */}

      {/* Table des factures */}
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>N° facture</th>
              <th>Client</th>
              <th>Date</th>
              <th>Montant HT (€)</th>
              <th>TVA</th>
              <th>Montant TTC (€)</th>
              <th>Restant dû (€)</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {factures.map(facture => (
              <tr key={facture.id}>
                <td>{facture.num_facture}</td>
                <td>{facture.client}</td>
                <td>{facture.date}</td>
                <td>{facture.montant_ht}</td>
                <td>{facture.tva}</td>
                <td>{facture.montant_ttc}</td>
                <td>{facture.restant_du}</td>
                <td>
                  {/* Replace these spans with actual buttons or icons */}
                  <span>Edit</span>
                  <span>Delete</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FactureAffichage;
