import React, { useState } from 'react';
import './Profile.css'; // Assurez-vous de créer et de lier ce fichier CSS

const ProfilePage = () => {
  const [formData, setFormData] = useState({
    nom: 'Sioudayyyyyy',
    prenom: 'Yassir',
    email: 'y@y',
    dateNaissance: '2001-02-16',
    telephonePortable: '0700000000',
    telephoneFixe: '0700000000',
    adresse: '210 Quai de Bercy, 75012 Paris',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Ici, vous enverrez les données à votre base de données via Supabase
    console.log(formData);
    // Mettre en place l'appel API pour mettre à jour les informations de l'utilisateur
  };

  return (
    <div className="profile-page-container">
      <h1>MON COMPTE</h1>
      <h2>MES INFORMATIONS</h2>
      <form className="profile-form" onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-field">
            <label>Nom</label>
            <input
              type="text"
              name="nom"
              value={formData.nom}
              onChange={handleChange}
            />
          </div>
          <div className="form-field">
            <label>Prénom</label>
            <input
              type="text"
              name="prenom"
              value={formData.prenom}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-field">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </div>

        <div className="form-row">
          <div className="form-field">
            <label>Date de naissance</label>
            <input
              type="date"
              name="dateNaissance"
              value={formData.dateNaissance}
              onChange={handleChange}
            />
          </div>
          <div className="form-field">
            <label>Téléphone portable</label>
            <input
              type="tel"
              name="telephonePortable"
              value={formData.telephonePortable}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className="form-field">
          <label>Adresse</label>
          <input
            type="text"
            name="adresse"
            value={formData.adresse}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="submit-btn">Enregistrer mes informations</button>
      </form>
    </div>
  );
};

export default ProfilePage;
