// ProfilePage.jsx
import React, { useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import './Profile.css'; // Votre fichier CSS pour ProfilePage

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    Nom: '',
    Prénom: '',
    email: '',
    DateNaissance: '',
    Téléphone: '',
    Adresse: '',
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Conversion des données si nécessaire
    // Assurez-vous que ces conversions correspondent à ce que votre BDD attend
    const dateNaissanceTimestamp = new Date(profile.DateNaissance).getTime();
    const telephoneNumber = parseInt(profile.Téléphone, 10);

    // Mise à jour du profil dans la BDD Supabase
    const { data, error } = await supabase
      .from('Authentification')
      .update({
        Nom: profile.Nom,
        Prénom: profile.Prénom,
        DateNaissance: dateNaissanceTimestamp,
        Téléphone: telephoneNumber,
        Adresse: profile.Adresse,
      })
      .match({ email: profile.email });

    if (error) {
      console.error('Erreur lors de la mise à jour:', error);
    } else {
      console.log('Profil mis à jour:', data);
      // Vous pouvez également définir ici une logique pour informer l'utilisateur de la mise à jour réussie
    }
  };

   return (
    <div className="app">
      <Menu />
      <div className="profile-page-container">
        <h1>Mon Compte</h1>
        <div className="profile-fields">
          <div className="field">
            <label>Nom</label>
            <div className="value">{profile.Nom}</div>
          </div>
          <div className="field">
            <label>Prénom</label>
            <div className="value">{profile.Prénom}</div>
          </div>
          <div className="field">
            <label>Email</label>
            <div className="value">
{profile.email}</div>
</div>
<div className="field">
<label>Date de naissance</label>
<div className="value">{new Date(profile.DateNaissance).toLocaleDateString()}</div>
</div>
<div className="field">
<label>Téléphone</label>
<div className="value">{profile.Téléphone}</div>
</div>
<div className="field">
<label>Adresse</label>
<div className="value">{profile.Adresse}</div>
</div>
{/* Ajoutez d'autres champs si nécessaire */}
</div>
</div>
</div>
);
};

export default ProfilePage;
