import React, { useState, useEffect } from 'react';
import  supabase  from '../../supabase'; // Assurez-vous que le chemin est correct
import './FactureAffichage.css'; // Assurez-vous que le chemin est correct

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  const [editFactureId, setEditFactureId] = useState(null);
  const [editFormData, setEditFormData] = useState({
    Client: '',
    DelaiDeReglement: '',
    DateEdition: '',
    Adresse: '',
    CodePostal: '',
    Ville: '',
    ComplementAdresse: '',
    Pays: '',
    Commentaire: '',
    TotalHT: '',
    TVA: '',
    TotalTTC: '',
  });

  useEffect(() => {
    const fetchFactures = async () => {
      const { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur lors du chargement des factures:', error);
      } else {
        setFactures(data);
      }
    };
    fetchFactures();
  }, []);

  const handleEditClick = (facture) => {
    setEditFactureId(facture.id);
    setEditFormData({
      Client: facture.Client,
      DelaiDeReglement: facture.DelaiDeReglement,
      DateEdition: facture.DateEdition,
      Adresse: facture.Adresse,
      CodePostal: facture.CodePostal,
      Ville: facture.Ville,
      ComplementAdresse: facture.ComplementAdresse || '',
      Pays: facture.Pays,
      Commentaire: facture.Commentaire || '',
      TotalHT: facture.TotalHT,
      TVA: facture.TVA,
      TotalTTC: facture.TotalTTC,
    });
  };

  const handleEditFormChange = (event) => {
    const { name, value } = event.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleEditFormSubmit = async (event) => {
    event.preventDefault();
    const updatedFacture = {
      ...editFormData,
    };
    const { error } = await supabase
      .from('Facture')
      .update(updatedFacture)
      .match({ id: editFactureId });

    if (error) {
      console.error('Erreur lors de la mise Ã  jour de la facture:', error);
    } else {
      const newFactures = factures.map((facture) => {
        if (facture.id === editFactureId) return { ...updatedFacture, id: editFactureId };
        return facture;
      });
      setFactures(newFactures);
      setEditFactureId(null);
    }
  };

  const handleCancelEdit = () => {
    setEditFactureId(null);
  };

  const handleDeleteClick = async (factureId) => {
    const { error } = await supabase
      .from('Facture')
      .delete()
      .match({ id: factureId });

    if (error) {
      console.error('Erreur lors de la suppression de la facture:', error);
    } else {
      const newFactures = factures.filter((facture) => facture.id !== factureId);
      setFactures(newFactures);
    }
  };

  return (
    <div className="facture-affichage">
      <h1>Gestion des Factures</h1>
      {factures.map((facture) => (
        <div key={facture.id} className="facture-item">
          {editFactureId === facture.id ? (
            <form onSubmit={handleEditFormSubmit} className="edit-form">
              <input
                type="text"
                name="Client"
                value={editFormData.Client}
                onChange={handleEditFormChange}
              />
              <input
                type="date"
                name="DelaiDeReglement"
                value={editFormData.DelaiDeReglement}
                onChange={handleEditFormChange}
              />
              <input
                type="date"
                name="DateEdition"
                value={editFormData.DateEdition}
                onChange={handleEditFormChange}
              />
              <input
                type="text"
                name="Adresse"
                value={editFormData.Adresse}
                onChange={handleEditFormChange}
              />
              <input
                type="text"
                name="CodePostal"
                value={editFormData.CodePostal}
                onChange={handleEditFormChange}
              />
              <input
                type="text"
                name="Ville"
                value={editFormData.Ville}
                onChange={handleEditFormChange}
              />
              <input
                type="text"
                name="ComplementAdresse"
                value={editFormData.ComplementAdresse}
                onChange={handleEditFormChange}
              />
              <input
                type="text"
                name="Pays"
                value={editFormData.Pays}
                onChange={handleEditFormChange}
              />
              <textarea
                name="Commentaire"
                value={editFormData.Commentaire}
                onChange={handleEditFormChange}
                ></textarea>
                <input
                  type="number"
                  name="TotalHT"
                  value={editFormData.TotalHT}
                  onChange={handleEditFormChange}
                />
                <input
                  type="number"
                  name="TVA"
                  value={editFormData.TVA}
                  onChange={handleEditFormChange}
                />
                <input
                  type="number"
                  name="TotalTTC"
                  value={editFormData.TotalTTC}
                  onChange={handleEditFormChange}
                />
                <div className="form-actions">
                  <button type="submit" className="save-btn">Sauvegarder</button>
                  <button type="button" onClick={handleCancelEdit} className="cancel-btn">Annuler</button>
                </div>
              </form>
            ) : (
              <div className="view-row">
                <div>{facture.Client}</div>
                <div>{facture.DelaiDeReglement}</div>
                <div>{facture.DateEdition}</div>
                <div>{facture.Adresse}</div>
                <div>{facture.CodePostal}</div>
                <div>{facture.Ville}</div>
                <div>{facture.ComplementAdresse}</div>
                <div>{facture.Pays}</div>
                <div>{facture.Commentaire}</div>
                <div>{facture.TotalHT}</div>
                <div>{facture.TVA}</div>
                <div>{facture.TotalTTC}</div>
                <div className="action-buttons">
                  <button onClick={() => handleEditClick(facture)} className="edit-btn">Modifier</button>
                  <button onClick={() => handleDeleteClick(facture.id)} className="delete-btn">Supprimer</button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    );
  };

  export default FactureAffichage;
