import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient'; // Assurez-vous que le chemin d'accès est correct

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    nom: '',
    prenom: '',
    dateNaissance: '',
    telephonePortable: '',
    adresse: '',
  });

  // Charger les informations de profil existantes
  useEffect(() => {
    const loadProfile = async () => {
      const user = supabase.auth.user();
      if (user) {
        const { data, error } = await supabase
          .from('Authentification')
          .select('*')
          .eq('id', user.id)
          .single();

        if (error) {
          console.error('Erreur lors du chargement du profil:', error);
        } else {
          setProfile({
            nom: data.nom || '',
            prenom: data.prenom || '',
            dateNaissance: data.date_de_naissance || '',
            telephonePortable: data.telephone_portable || '',
            adresse: data.adresse || '',
          });
        }
      }
    };

    loadProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = supabase.auth.user();

    if (user) {
      const { data, error } = await supabase
        .from('Authentification')
        .update({
          nom: profile.nom,
          prenom: profile.prenom,
          date_de_naissance: profile.dateNaissance,
          telephone_portable: profile.telephonePortable,
          adresse: profile.adresse,
        })
        .eq('id', user.id);

      if (error) {
        console.error('Erreur lors de la mise à jour du profil:', error);
      } else {
        console.log('Profil mis à jour avec succès:', data);
        // Vous pouvez ici informer l'utilisateur du succès de la mise à jour.
      }
    }
  };

  return (
    <div className="profile-page">
      <h2>Profil</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="nom"
          value={profile.nom}
          onChange={handleChange}
          placeholder="Nom"
        />
        <input
          type="text"
          name="prenom"
          value={profile.prenom}
          onChange={handleChange}
          placeholder="Prénom"
        />
        <input
          type="date"
          name="dateNaissance"
          value={profile.dateNaissance}
          onChange={handleChange}
        />
        <input
          type="tel"
          name="telephonePortable"
          value={profile.telephonePortable}
          onChange={handleChange}
          placeholder="Téléphone portable"
        />
        <textarea
          name="adresse"
          value={profile.adresse}
          onChange={handleChange}
          placeholder="Adresse"
        />
        <button type="submit">Mettre à jour</button>
      </form>
    </div>
  );
};

export default ProfilePage;
