import React, { useEffect, useState } from 'react';
import supabase from '../../supabase';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import styles from  './Profile.css'; // Votre fichier CSS pour ProfilePage


function ProfilePage() {
  const [profile, setProfile] = useState({
    nom: '', prenom: '', email: '', adresse: '', dateNaissance: '', telephone: ''
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    const user = supabase.auth.user();
    if (user) {
      let { data, error } = await supabase
        .from('Authentification')
        .select('*')
        .eq('id', user.id)
        .single();
      if (error) {
        console.error('Erreur lors du chargement du profil', error);
        return;
      }
      setProfile({ ...data });
    }
    setLoading(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    const user = supabase.auth.user();
    const { error } = await supabase
      .from('Authentification')
      .update({ ...profile })
      .eq('id', user.id);
    if (error) {
      alert('Erreur lors de la mise à jour', error.message);
    } else {
      alert('Profil mis à jour avec succès');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  return (
    <div className={styles.profilePage}>
      <Menu />
      <form onSubmit={handleSubmit} className={styles.profileForm}>
        <div className={styles.formGroup}>
          <label htmlFor="nom">Nom</label>
          <input type="text" name="nom" id="nom" value={profile.nom} onChange={handleChange} />
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="prenom">Prénom</label>
          <input type="text" name="prenom" id="prenom" value={profile.prenom} onChange={handleChange} />
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="email">Email</label>
          <input type="email" name="email" id="email" value={profile.email} onChange={handleChange} />
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="adresse">Adresse</label>
          <input type="text" name="adresse" id="adresse" value={profile.adresse} onChange={handleChange} />
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="dateNaissance">Date de Naissance</label>
          <input type="date" name="dateNaissance" id="dateNaissance" value={profile.dateNaissance} onChange={handleChange} />
        </div>
        <div className={styles.formGroup}>
          <label htmlFor="telephone">Téléphone</label>
          <input type="tel" name="telephone" id="telephone" value={profile.telephone} onChange={handleChange} />
        </div>
        <button type="submit" disabled={loading} className={styles.submitButton}>
          Mettre à jour
        </button>
      </form>
    </div>
  );
}

export default ProfilePage;
