import React, { useState } from 'react';
import supabase from '../../supabase';
const ProfilePage = () => {
  const [profile, setProfile] = useState({
    nom: '',
    prenom: '',
    email: '',
    dateNaissance: '',
    telephonePortable: '',
    telephoneFixe: '',
    adresse: '',
  });

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase
      .from('Authentification') // Remplacez par le nom exact de votre table
      .update({
        nom: profile.nom,
        prenom: profile.prenom,
        // email: profile.email, // L'email ne devrait pas être changé sans vérification supplémentaire
        date_de_naissance: profile.dateNaissance,
        telephone_portable: profile.telephonePortable,
        telephone_fixe: profile.telephoneFixe,
        adresse: profile.adresse,
      })
      .eq('email', profile.email); // Utilisez l'email ou un autre identifiant unique pour la mise à jour

    if (error) {
      console.error('Erreur lors de la mise à jour:', error);
    } else {
      console.log('Profil mis à jour:', data);
      // Afficher un message de succès ou effectuer une autre action
    }
  };

  return (
    <div className="profile-page-container">
      <h1>Mon Compte</h1>
      <h2>Mes Informations</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Nom:
          <input
            type="text"
            name="nom"
            value={profile.nom}
            onChange={handleChange}
          />
        </label>
        <label>
          Prénom:
          <input
            type="text"
            name="prenom"
            value={profile.prenom}
            onChange={handleChange}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
          />
        </label>
        <label>
          Date de naissance:
          <input
            type="date"
            name="dateNaissance"
            value={profile.dateNaissance}
            onChange={handleChange}
          />
        </label>
        <label>
          Téléphone portable:
          <input
            type="tel"
            name="telephonePortable"
            value={profile.telephonePortable}
            onChange={handleChange}
          />
        </label>
        <label>
          Téléphone fixe:
          <input
            type="tel"
            name="telephoneFixe"
            value={profile.telephoneFixe}
            onChange={handleChange}
          />
        </label>
        <label>
          Adresse:
          <input
            type="text"
            name="adresse"
            value={profile.adresse}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Enregistrer mes informations</button>
      </form>
    </div>
  );
};

export default ProfilePage;
