import React, { useState } from 'react';
import './Menu.css';
import {
  FaRegChartBar,
  FaFileAlt,
  FaBell,
  FaCog,
  FaSignOutAlt,
  FaUser,
  FaQuestionCircle,
  FaBars // Assuming you have this icon for the hamburger menu
} from 'react-icons/fa';

const App = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app">
      {/* Hamburger menu button */}
      <button className="hamburger-menu" onClick={toggleSidebar}>
        <FaBars />
      </button>

      {/* Sidebar */}
      <aside className={`sidebar ${isSidebarOpen ? 'active' : ''}`}>
        <div className="logo-container">
          <span className="logo">R</span>
          <span className="logo-text">Rimberio</span>
        </div>
        <nav className="navigation">
          <ul className="nav-links">
            <li><a href="#" className="nav-link active"><FaRegChartBar /> Dashboard</a></li>
            <li><a href="#" className="nav-link"><FaRegChartBar /> Statistics</a></li>
            <li><a href="#" className="nav-link"><FaFileAlt /> Report</a></li>
            <li><a href="#" className="nav-link"><FaBell /> Notification</a></li>
            <li><a href="#" className="nav-link"><FaCog /> Settings</a></li>
            <li><a href="#" className="nav-link"><FaSignOutAlt /> Logout</a></li>
          </ul>
        </nav>
        <div className="sidebar-footer">
          <a href="#" className="footer-link"><FaUser /> Account</a>
          <a href="#" className="footer-link"><FaQuestionCircle /> Help</a>
        </div>
      </aside>

      {/* Main content */}
      <div className="content">
        {/* Content goes here */}
      </div>
    </div>
  );
};

export default App;
