import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Importez useHistory depuis react-router-dom
import supabase from '../../supabase';

const Connexion = () => {
  const history = useHistory(); // Utilisez useHistory pour la navigation
  const [utilisateurs, setUtilisateurs] = useState([]);
  const [email, setEmail] = useState('');
  const [mdp, setMdp] = useState('');
  const [erreur, setErreur] = useState(null);

  // Récupérer les utilisateurs depuis la base de données
  useEffect(() => {
    fetchUtilisateurs();
  }, []);

  const fetchUtilisateurs = async () => {
    try {
      let { data: utilisateurs, error } = await supabase
        .from('utilisateurs')
        .select('email', 'mdp');

      if (error) throw error;

      setUtilisateurs(utilisateurs || []);
    } catch (error) {
      console.error('Erreur lors de la récupération des utilisateurs:', error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Vérifier si l'email et le mot de passe correspondent à un utilisateur dans la base de données
      const utilisateur = utilisateurs.find((utilisateur) => utilisateur.email === email && utilisateur.mdp === mdp);

      if (utilisateur) {
        console.log('Connexion réussie pour l\'utilisateur:', utilisateur.email);
        // Rediriger l'utilisateur vers la page de menu
        history.push('/menu');
      } else {
        throw new Error('Email ou mot de passe incorrect');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion:', error.message);
      setErreur(error.message);
    }
  };

  return (
    <div className="login-page">
      <div className="form-section">
        <div className="form-container">
          <h1 className="form-title">Connexion</h1>
          <form className="login-form" onSubmit={handleSubmit}>
            <div className="input-group">
              <input 
                type="email" 
                placeholder="Adresse mail" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                required 
              />
            </div>
            <div className="input-group">
              <input 
                type="password" 
                placeholder="Mot de passe" 
                value={mdp} 
                onChange={(e) => setMdp(e.target.value)} 
                required 
              />
            </div>
            <div className="action-group">
              <button type="submit" className="submit-btn">Je me connecte</button>
              <p className="alternative-action">Pas encore de compte ? <a href="/inscriptions" className="link">S'inscrire</a></p>
            </div>
          </form>
          {erreur && <div style={{ color: 'red' }}>{erreur}</div>}
        </div>
      </div>
      <div className="background-image-section"></div>
    </div>
  );
};

export default Connexion;
