import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Assurez-vous que le chemin d'accès est correct
import './Profiles.css'; // Votre fichier CSS pour ProfilePage
import supabase from '../../supabase';

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    Nom: '',
    Prénom: '',
    email: '',
    DateNaissance: '',
    Téléphone: '',
    Adresse: '',
  });

  useEffect(() => {
    // Vous devez remplacer 'userId' par l'identifiant de l'utilisateur que vous obtenez de votre logique d'authentification ou de votre state management (comme Redux, Context, etc.)
    const userId = 'userId'; // Cet identifiant doit être passé au composant ou récupéré depuis un contexte global ou un store Redux

    const fetchUserData = async () => {
      const { data, error } = await supabase
        .from('Authentification')
        .select('*')
        .eq('id', userId) // Remplacez 'id' par la clé primaire ou la colonne d'identification unique de votre utilisateur
        .single();

      if (error) {
        console.error('Erreur lors de la récupération des données utilisateur:', error);
      } else if (data) {
        setProfile({
          Nom: data.Nom,
          Prénom: data.Prénom,
          email: data.email,
          DateNaissance: data.DateNaissance, // Assurez-vous de transformer cette date en chaîne de caractères si nécessaire
          Téléphone: data.Téléphone,
          Adresse: data.Adresse,
        });
      }
    };

    fetchUserData();
  }, []); // Le tableau vide indique que cet effet ne s'exécute qu'au montage du composant

  // ... (les autres parties de votre composant, y compris handleChange et handleSubmit)

  return (
    <div className="app-container">
      <Menu />
      <div className="profile-page-container">
        <h1>Mon Compte</h1>
        <h2>Mes Informations</h2>
        <form onSubmit={handleSubmit}>
          {/* Champs de formulaire pour afficher les informations de l'utilisateur */}
          <label>
            Nom:
            <input
              type="text"
              name="Nom"
              value={profile.Nom || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          <label>
            Prénom:
            <input
              type="text"
              name="Prénom"
              value={profile.Prénom || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={profile.email || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          <label>
            Date de naissance:
            <input
              type="date"
              name="DateNaissance"
              value={profile.DateNaissance || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          <label>
            Téléphone:
            <input
              type="tel"
              name="Téléphone"
              value={profile.Téléphone || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          <label>
            Adresse:
            <input
              type="text"
              name="Adresse"
              value={profile.Adresse || ''}
              onChange={handleChange}
              readOnly
            />
          </label>
          {/* Supprimez le bouton de soumission si le formulaire est en lecture seule */}
        </form>
      </div>
    </div>
  );
};

export default ProfilePage;
