import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu';
import supabase  from '../../supabase';
import './FactureAffichage.css';

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);

  useEffect(() => {
    async function getFactures() {
      const { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur de chargement des factures', error);
      } else {
        setFactures(data);
      }
    }

    getFactures();
  }, []);

  return (
    <div className="app">
      <Menu />
      <div className="facture-content">
        <h1>GESTION DES FACTURES</h1>
        {/* ... Votre code pour l'interface de filtre, d'impression, d'exportation, etc. */}

        {/* Affichage des factures */}
        <div className="facture-list">
          {factures.map(facture => (
            <div key={facture.id} className="facture-item">
              <div>{facture.Client}</div>
              <div>{facture.DateEdition}</div>
              <div>{facture.TotalHT}</div>
              <div>{facture.TVA}</div>
              <div>{facture.TotalTTC}</div>
              {/* ... Autres champs de facture */}
              <div>
                {/* Boutons d'action pour chaque facture */}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default FactureAffichage;
