import React, { useState, useEffect } from 'react';
import Menu from '../Menu/Menu'; // Vérifiez le chemin
import { supabase } from '../../supabase'; // Vérifiez le chemin
import './FactureAffichage.css'; // Assurez-vous que ce chemin est correct

const FactureAffichage = () => {
  const [factures, setFactures] = useState([]);
  const [editFactureId, setEditFactureId] = useState(null);
  const [editFormData, setEditFormData] = useState({
    Client: '',
    DelaiDeReglement: '',
    DateEdition: '',
    Adresse: '',
    CodePostal: '',
    Ville: '',
    ComplementAdresse: '',
    Pays: '',
    Commentaire: '',
    TotalHT: '',
    TVA: '',
    TotalTTC: '',
  });

  useEffect(() => {
    const fetchFactures = async () => {
      const { data, error } = await supabase.from('Facture').select('*');
      if (error) {
        console.error('Erreur lors du chargement des factures:', error);
      } else {
        setFactures(data);
      }
    };

    fetchFactures();
  }, []);

  const handleEditClick = (facture) => {
    setEditFactureId(facture.id);
    setEditFormData(facture);
  };

  const handleEditFormChange = (event) => {
    const { name, value } = event.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  const handleEditFormSubmit = async (event) => {
    event.preventDefault();

    const updatedFacture = {
      id: editFactureId,
      ...editFormData,
    };

    const { error } = await supabase
      .from('Facture')
      .update(updatedFacture)
      .match({ id: editFactureId });

    if (error) {
      console.error('Erreur lors de la mise à jour de la facture:', error);
    } else {
      const updatedFactures = factures.map((facture) => {
        if (facture.id === editFactureId) return updatedFacture;
        return facture;
      });

      setFactures(updatedFactures);
      setEditFactureId(null);
    }
  };

  const handleCancelClick = () => {
    setEditFactureId(null);
  };

  return (
    <div className="app">
      <Menu />
      <div className="facture-content">
        <h1>Gestion des Factures</h1>
        {factures.map((facture) => (
          <form onSubmit={handleEditFormSubmit} key={facture.id}>
            {editFactureId === facture.id ? (
              <div className="edit-form">
                {/* Inputs pour chaque champ à éditer */}
                <input
                  type="text"
                  name="Client"
                  defaultValue={facture.Client}
                  onChange={handleEditFormChange}
                />
                {/* Répétez pour les autres champs */}
                <button type="submit">Enregistrer</button>
                <button type="button" onClick={handleCancelClick}>
                  Annuler
                </button>
              </div>
            ) : (
              <div className="view-row">
                {/* Affichage des données de la facture */}
                <div>{facture.Client}</div>
                {/* Répétez pour les autres données */}
                <button type="button" onClick={() => handleEditClick(facture)}>
                  Modifier
                </button>
              </div>
            )}
          </form>
        ))}
      </div>
    </div>
  );
};

export default FactureAffichage;
