import React, { useState } from 'react';
import './Menu.css';
import logo from '../images/InvoicePro.png'

import {
  FaRegChartBar,
  FaFileAlt,
  FaBell,
  FaCog,
  FaSignOutAlt,
  FaUser,
  FaQuestionCircle,
  FaBars // Assuming you have this icon for the hamburger menu
} from 'react-icons/fa';

const App = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app">
      {/* Hamburger menu button */}

      <button className="hamburger-menu" onClick={toggleSidebar}>
        <FaBars />
      </button>

      {/* Sidebar */}
      <aside className={`sidebar ${isSidebarOpen ? 'active' : ''}`}>
        <div className="logo-container">
        <img src={logo} alt="Logo" className="logo" /> 
        </div>
        <nav className="navigation">
          <ul className="nav-links">
            <li><a href="#" className="nav-link active"><FaRegChartBar /> Facture</a></li>
            <li><a href="#" className="nav-link"><FaRegChartBar /> Devis</a></li>

          </ul>
        </nav>
        <div className="sidebar-footer">
          <a href="/profiles" className="footer-link"><FaUser /> Profile</a>

        </div>
      </aside>

      {/* Main content */}
      <div className="content">
        {/* Content goes here */}
      </div>
    </div>
  );
};

export default App;
