import React, { useState, useEffect } from 'react';
import supabase from '../../supabase';

const ProfilePage = () => {
  const [profile, setProfile] = useState({
    email: '', // Vous devez connaître l'email pour identifier l'utilisateur
    nom: '',
    prenom: '',
    dateNaissance: '',
    telephonePortable: '',
    adresse: '',
  });

  useEffect(() => {
    // Supposons que vous avez un moyen d'obtenir l'email de l'utilisateur connecté
    const userEmail = 'user@example.com';

    // Charger les informations de profil existantes
    const loadProfile = async () => {
      const { data, error } = await supabase
        .from('Authentification')
        .select('*')
        .eq('email', userEmail)
        .single();

      if (error) {
        console.error('Erreur lors du chargement du profil:', error);
      } else {
        setProfile({
          email: data.email,
          nom: data.nom || '',
          prenom: data.prenom || '',
          dateNaissance: data.date_de_naissance || '',
          telephonePortable: data.telephone_portable || '',
          adresse: data.adresse || '',
        });
      }
    };

    loadProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, ...updates } = profile;

    const { data, error } = await supabase
      .from('Authentification')
      .update(updates)
      .eq('email', email);

    if (error) {
      console.error('Erreur lors de la mise à jour du profil:', error);
    } else {
      console.log('Profil mis à jour avec succès:', data);
      // Informer l'utilisateur de la mise à jour réussie
    }
  };

  return (
    <div className="profile-page">
      <h2>Profil</h2>
      <form onSubmit={handleSubmit}>
        {/* Votre JSX pour le formulaire, en utilisant profile et handleChange */}
        {/* ... */}
        <button type="submit">Mettre à jour</button>
      </form>
    </div>
  );
};

export default ProfilePage;
