import React, { useState } from 'react';
import supabase from '../../supabase'; // Assurez-vous que le chemin est correct

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // Requête pour vérifier les identifiants dans la table Connexion
      const { data, error } = await supabase
        .from('Connexion')
        .select('email, mdp')
        .eq('email', email)
        .single();

      if (error) {
        throw error;
      }

      // Vérifier si l'enregistrement existe et si le mot de passe correspond
      if (data && data.mdp === password) {
        console.log('Connexion réussie pour:', data.email);
        // Rediriger l'utilisateur vers la page de menu ou effectuer d'autres actions après la connexion réussie
      } else {
        throw new Error('Identifiants incorrects');
      }
    } catch (error) {
      console.error('Erreur de connexion:', error.message);
      setError('Erreur de connexion. Veuillez vérifier vos identifiants.');
    }
  };

  return (
    <div>
      <h2>Connexion</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Mot de passe:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </label>
        </div>
        {error && <div style={{ color: 'red' }}>{error}</div>}
        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
};

export default LoginPage;
